<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Manage_library_Model extends CI_Model  {  	public function insert_image_library($file)	{		$system_ip 	= $this->input->ip_address();		$time 		= time();		$date 		= date("Y-m-d",$time);		$user_id 	= $this->session->userdata("user_id");				$upload_path 		= "./uploads/manage_library/photo/main/";		$upload_resize 		= "./uploads/manage_library/photo/resize/";				$extension = pathinfo($file["name"], PATHINFO_EXTENSION);		if($extension=="mp4")		{			$image = $file["name"];			$file_tmp =$file['tmp_name'];			move_uploaded_file($file_tmp,$upload_path.$file["name"]);		}else{			$this->Image_Model->uploadTo = $upload_path;			$image = $this->Image_Model->upload($file);			$image = str_replace($upload_path,"",$image);					$this->Image_Model->newPath 	= $upload_resize;			$this->Image_Model->newWidth 	= 550;			$this->Image_Model->newHeight 	= 550;			$this->Image_Model->resize();		}				$dt = array(					'image'=>$image,					'date'=>$date,					'time'=>$time,					'update_date'=>$date,					'update_time'=>$time,					'system_ip'=>$system_ip,					'user_id'=>$user_id,				);				$id = $this->Scheme_Model->insert_fun("tbl_library",$dt);		return $id;	}		public function update_image_library($file)	{	}		}  