ion(data){			if(data!="")			{				$('.get_field_group_set_page_div').html(data);				$('.chosen-select').chosen({width: "100%"})			}								else			{				java_alert_function("error","Something Wrong")				$('.url_error').html("Something Wrong");			}		}	});}function delete_field_group_set(id){	if (confirm('Are you sure Delete?')) { 	$.ajax({		type       : "POST",		data       :  {id:id,},		url        : "<?= base_url()?>admin/<?= $Page_name; ?>/delete_field_group_set",		success    : function(data){				if(data!="")				{					java_alert_function("success","Delete Successfully");					$("#row1_"+id).hide("500");				}									else				{					java_alert_function("error","Something Wrong")				}			}		});	}}</script>