<div class="row">	<div class="col-xs-12" style="margin-bottom:20px;">		<button type="button" class="btn btn-w-m btn-info" onclick="goBack();">		<< Back</button>	</div>	<form class="form-horizontal" role="form" method="post" enctype="multipart/form-data">		<div class="col-xs-9">			<div class="ibox float-e-margins">				<div class="ibox-content">					<div class="form-group">						<div class="col-sm-12">							<div class="col-sm-2 text-right">								<label class="control-label" for="form-field-1">									Title								</label>							</div>							<div class="col-sm-10">								<input type="text" class="form-control title" id="form-field-1" placeholder="Title" name="title" value="<?php echo $title; ?>" onchange="url_change()" />							</div>						</div>					</div>										<div class="form-group">						<div class="col-sm-12">							<div class="col-sm-2 text-right">								<label class="control-label" for="form-field-1">									Description								</label>							</div>							<div class="col-sm-10">								<textarea type="text" class="form-control summernote" id="form-field-1" placeholder="Description" name="description" style="height:100px"><?php echo $description; ?></textarea>							</div>						</div>					</div>										<div class="form-group">						<div class="col-sm-12">							<div class="col-sm-2 text-right">								<label class="control-label" for="form-field-1">									Excerpt								</label>							</div>							<div class="col-sm-10">								<textarea type="text" class="form-control" id="form-field-1" placeholder="Excerpt" name="excerpt" style="height:100px"><?php echo $excerpt; ?></textarea>							</div>						</div>					</div>				</div>			</div>		</div>		<div class="col-xs-3">			<div class="ibox float-e-margins">				<div class="ibox-content">					<div class="form-group">						<div class="col-sm-12">							<div class="col-sm-3 text-right">								<label class="control-label" for="form-field-1">									Url								</label>							</div>							<div class="col-sm-9">								<input type="text" class="form-control url" id="form-field-1" placeholder="Url" name="url" value="<?php echo $url; ?>" />							</div>						</div>						<div class="col-sm-12">							<span class="url1"></span>						</div>					</div>										<div class="form-group">						<div class="col-sm-12">							<div class="col-sm-3 text-right">								<label class="control-label" for="form-field-1">									Image								</label>							</div>							<div class="col-sm-9">								<input type="file" class="form-control" id="form-field-1" placeholder="image" name="image" />							</div>						</div>					</div>										<div class="form-group">						<div class="col-sm-12">							<div class="col-sm-3 text-right">								<label class="control-label" for="form-field-1">									Mobile Image								</label>							</div>							<div class="col-sm-9">								<input type="file" class="form-control" id="form-field-1" placeholder="Mobile Image" name="mobile_image" />							</div>						</div>					</div>															<div class="form-group">						<div class="col-sm-12">							<div class="col-sm-3 text-right">								<label class="control-label" for="form-field-1">									Status								</label>							</div>							<div class="col-sm-9">								<select name="status" id="status" data-placeholder="Select Status" class="chosen-select" >									<option value="1" <?php if($status==1) { ?> selected <?php } ?>>										Active									</option>									<option value="0" <?php if($status==0) { ?> selected <?php } ?>>										Inactive									</option>								</select>							</div>							<div class="help-inline col-sm-12 has-error">								<span class="help-block reset middle">  									<?= form_error('status'); ?>								</span>							</div>						</div>					</div>										<div class="space-4"></div>					<br /><br />					<div class="clearfix form-actions">						<div class="col-md-offset-3 col-md-9">							<button type="submit" class="btn btn-info submit_button" name="Submit">								<i class="ace-icon fa fa-check bigger-110"></i>								Submit							</button>						</div>					</div>				</div>			</div>		</div>	</form></div><!-- /.row --><script>function url_change(){	title = $(".title").val();	title = title.replace(/&/g,'and');	title = title.trim(title).replace(/ /g,'-');	title = encodeURI(title).replace(/[!\/\\#,+()$~%.'":*?<>{}]/g,'');	$(".url").val(title)	v = '<?= base_url(); ?>'+title;	url1 = "Permalink : <a href='"+v+"' target='_blank'>"+v+"</a>";	$(".url1").html(url1)	change_url(title)}function change_url(url){	id = 0;	$.ajax({	type       : "POST",	data       :  {url:url,id:id,} ,	url        : "<?= base_url()?>admin/<?= $Page_name?>/check_url",	success    : function(data){			if(data!="")			{				//java_alert_function("success","Delete Successfully");				//$('.product_code_error').html("This Product SKU Already Taken");				if(data=="Error")				{					java_alert_function("error","This Url Already Taken")					$('.url_error').html("This Url Already Taken");				}				if(data=="ok")				{					java_alert_function("success","Url Ok");					$('.url_error').html("Url Ok");					disabled_submit_button();				}			}								else			{				java_alert_function("error","Something Wrong")				$('.url_error').html("Something Wrong");			}		}	});}function disabled_submit_button(){	if(error2==1)	{		$(".submit_button").prop('disabled', false);	}	else	{		$(".submit_button").prop("disabled", true);	}}</script>