<div class="row">	<div class="col-xs-12" style="margin-bottom:20px;">		<a href="<?= base_url()?>admin/<?= $Page_name; ?>" class="btn btn-w-m btn-info">			Back		</a>	</div>	<form class="form-horizontal" role="form" method="post" enctype="multipart/form-data">		<div class="col-xs-9">			<div class="ibox float-e-margins">				<div class="ibox-content">					<div class="form-group">						<div class="col-sm-12">							<label class="control-label" for="form-field-1">								Title							</label>						</div>						<div class="col-sm-12">							<input type="text" class="form-control title" id="form-field-1" placeholder="Title" name="title" value="<?php echo $title; ?>" onchange="url_change()" />						</div>					</div>									</div>			</div>		</div>		<div class="col-xs-3">			<div class="ibox float-e-margins">				<div class="ibox-content">					<?php $this->Manage_field_group_model->get_status_or_submit_button(set_value('status'),"Submit"); ?>				</div>			</div>		</div>	</form></div><!-- /.row --><script>function url_change(){	title = $(".title").val();	title = title.replace(/&/g,'and');	title = title.trim(title).replace(/ /g,'-');	title = encodeURI(title).replace(/[!\/\\#,+()$~%.'":*?<>{}]/g,'');	$(".url").val(title)	v = '<?= base_url(); ?>'+title;	url1 = "Permalink : <a href='"+v+"' target='_blank'>"+v+"</a>";	$(".url1").html(url1)	change_url(title)}function change_url(url){	id = 0;	$.ajax({	type       : "POST",	data       :  {url:url,id:id,} ,	url        : "<?= base_url()?>admin/<?= $Page_name?>/check_url",	success    : function(data){			if(data!="")			{				//java_alert_function("success","Delete Successfully");				//$('.product_code_error').html("This Product SKU Already Taken");				if(data=="Error")				{					java_alert_function("error","This Url Already Taken")					$('.url_error').html("This Url Already Taken");				}				if(data=="ok")				{					java_alert_function("success","Url Ok");					$('.url_error').html("Url Ok");					disabled_submit_button();				}			}								else			{				java_alert_function("error","Something Wrong")				$('.url_error').html("Something Wrong");			}		}	});}function disabled_submit_button(){	if(error2==1)	{		$(".submit_button").prop('disabled', false);	}	else	{		$(".submit_button").prop("disabled", true);	}}</script>